! function() {
  var a = document.querySelector("#header_tab_bar"),
    b = document.querySelectorAll(".header_tabs"),
    c = document.querySelectorAll(".header_tab_links");
  a.addEventListener("click", function(d) {
    if (d.target != a) {
      var e = Array.prototype.indexOf.call(b, d.target.parentNode);
      c[e].click()
    }
  })
}();
